	// HALFWIDTH_AND_FULLWIDTH_FORMS 判断中文的，号

	public static boolean isChinese(char c) {

		Character.UnicodeBlock ub = Character.UnicodeBlock.of(c);

		if (ub == Character.UnicodeBlock.CJK_UNIFIED_IDEOGRAPHS

		|| ub == Character.UnicodeBlock.CJK_COMPATIBILITY_IDEOGRAPHS

		|| ub == Character.UnicodeBlock.CJK_UNIFIED_IDEOGRAPHS_EXTENSION_A

		|| ub == Character.UnicodeBlock.GENERAL_PUNCTUATION

		|| ub == Character.UnicodeBlock.CJK_SYMBOLS_AND_PUNCTUATION

		|| ub == Character.UnicodeBlock.HALFWIDTH_AND_FULLWIDTH_FORMS) {

			return true;

		}

		return false;

	}

	public static int getChineseWordCount(String strName) {
		int nCount = 0;
		char[] ch = strName.toCharArray();

		for (int i = 0; i < ch.length; i++) {

			char c = ch[i];

			if (isChinese(c) == true) {
				nCount++;
			}

		}
		return nCount;
	}

	public static boolean isChinese(String strName) {

		char[] ch = strName.toCharArray();

		for (int i = 0; i < ch.length; i++) {

			char c = ch[i];

			if (isChinese(c) == true) {
				return true;

			}

		}
		return false;
	}

	/***
	 * 转成简体
	 * 
	 * @param st
	 * @return
	 */
	public static String simplized(String st) {
		StringBuffer sb = new StringBuffer();
		for (int i = 0; i < st.length(); i++) {
			char temp = st.charAt(i);
			int index = ft.indexOf(temp);
			if (index != -1)
				sb.append(jt.charAt(index));
			else
				sb.append(temp);
		}
		return sb.toString();
	}

	/***
	 * 转成繁体
	 * 
	 * @param st
	 * @return
	 */
	public static String traditionalized(String st) {

		StringBuffer sb = new StringBuffer();
		for (int i = 0; i < st.length(); i++) {
			char temp = st.charAt(i);
			int index = jt.indexOf(temp);
			if (index != -1)
				sb.append(ft.charAt(index));
			else
				sb.append(temp);
		}
		return sb.toString();
	}
